//User function Template for javascript

/**
 * @param {string} a
 * @param {string} b
 * @returns {string}
*/

class Solution
{
    //Function to join given strings
    comboString(a, b)
    {
        let longer = a.length > b.length ? a : b;
        let shorter= a.length < b.length ? a : b;

        return shorter + longer + shorter;
    }
}
