//Back-end complete function template for C++

class Solution{   
public:
    //Function to find the repeated sum of digits.
    int repeatedSumOfDigits(int N){
        //Calculating the remainder of N divided by 9.
        int res = (N % 9 == 0) ? 9 : (N % 9);        
        //Returning the result.
        return res;        
    }
};

