// Backend complete function template for c++
class Solution {
  public:
    // Returns the longest string in dictionary which is a
    // subsequence of str.
    string replaceAll(string str, string oldW, string newW) {
        if (oldW.empty()) return str;
        int start_pos = 0;
        while ((start_pos = str.find(oldW, start_pos)) != string::npos) {
            str.replace(start_pos, oldW.size(), newW);
            start_pos += newW.size(); // In case 'newW' contains 'oldW', like
                                      // replacing 'x' with 'yx'
        }
        return str;
    }
};

//User function Template for C#

class Solution
{
    //Complete this function
    public string replaceAll(string str, string oldW, string newW)
    {
        if (string.IsNullOrEmpty(oldW))
            return str;

        int start_pos = 0;
        while ((start_pos = str.IndexOf(oldW, start_pos)) != -1)
        {
            str = str.Remove(start_pos, oldW.Length).Insert(start_pos, newW);
            start_pos += newW.Length;
        }

        return str;
    }
}
