class Solution{
	//method to convert all occurrences of 0 to 5 in a given number
	public static int convertFive(int n){
		//if number is 0, return 5
		if(n==0)
			return 5;
		else{
			//call helper function to convert number
			return convert(n);
		}
	}

	//recursive helper function to convert all occurrences of 0 to 5 in a given number
	public static int convert(int n){
		//base case: if number is 0, return 0
		if(n==0)
			return 0;

		//get the last digit of the number
		int digit=n%10;

		//if last digit is 0, change it to 5
		if(digit==0)
			digit=5;

		//recursive call to convert remaining digits of the number
		//and add modified digit at the end
		return convert(n/10)*10+digit;
	}
}

