class Solution
{
  public:
    int replaceBit (int n, int k)
    {
        int temp = n;
        int pos = 0; // position of the rightmost set bit
    
        while (temp)
        {
            pos++;
            temp /= 2;
        }
    
        // if the number bits is less than k,
        // then we can't make any changes
        if (pos < k) return n; 
    
    
        // checking if the k-th bit is set or not
        if ((n & (1 << (pos - k))) != 0)
        {
            // if it's set, we make it unset and return the result
            return n - (1 << (pos - k));
        }
        // else, we make no change
        return n;
    }
    // Contributed By: Pranay Bansal

};

public class Solution
{
    public int replaceBit(int n, int k)
    {
        int temp = n;
        int pos = 0;

        while (temp != 0)
        {
            pos++;
            temp /= 2;
        }

        if (pos < k) return n; 

        if ((n & (1 << (pos - k))) != 0)
        {
            return n - (1 << (pos - k));
        }

        return n;
    }
}
