//Back-end complete function Template for C++

class Solution{
public:
    //Function to calculate the greatest common divisor of two numbers.
    int gcd(int a, int b){
        if(b == 0) //if b is 0, then gcd is a.
            return a;
        return gcd(b, a%b); //recursively find gcd using euclidean algorithm.
    }

    //Function to calculate the minimum number of rooms required.
    int rooms(int N, int M){
        int x = gcd(N, M); //calculating gcd of N and M.
        int ans = N/x + M/x; //calculating minimum number of rooms using gcd.
        return ans; //returning the minimum number of rooms.
    }
};

// Back-end complete function Template for Java

class Solution{
    // Function to find the greatest common divisor of two numbers
    static int gcd(int a, int b){
        if(b == 0)
            return a;
        return gcd(b, a%b);
    }

    // Function to calculate the number of rooms that can be made
    static int rooms(int N, int M){
        // Finding the greatest common divisor of N and M
        int x = gcd(N, M);
        // Calculating the number of rooms using the formula N/x + M/x
        int ans = N/x + M/x;
        // Returning the result
        return ans;
    }
}
