//Back-end complete function Template for C++

class Solution {
  public:
    void reverseArray(vector<int>& arr) {

        stack<int> s;
        for (int i = 0; i < arr.size(); i++) {
            s.push(arr[i]);
        }
        int j = 0;
        while (!s.empty()) {
            arr[j++] = s.top();
            s.pop();
        }
    }
};
