class Solution:

    def modifyQueue(self, q, k):
        stack = []
        new_queue = Queue()

        # Pop first k elements from queue and push into stack
        for _ in range(k):
            stack.append(q.get())

        # Push elements from stack back into the queue
        while stack:
            new_queue.put(stack.pop())

        # Add the remaining elements from original queue to the new queue
        while not q.empty():
            new_queue.put(q.get())

        return new_queue

