class Solution {
    modifyQueue(q, k) {
        let stack = [];
        let newQueue = [];

        // Pop first k elements from queue and push into stack
        for (let i = 0; i < k; i++) {
            stack.push(q.shift());
        }

        // Push elements from stack back into the queue
        while (stack.length > 0) {
            newQueue.push(stack.pop());
        }

        // Add the remaining elements from original queue to the new queue
        while (q.length > 0) {
            newQueue.push(q.shift());
        }

        return newQueue;
    }
}
