//Back-end complete function Template for C

void reverseString(char str[])
{
    int g, numb;
    int temp = 0;

    for(numb = 0; str[numb] != 0; numb++);
    for(g = 0; g < numb/2; g++)
    {
        temp = str[g];
        str[g]=str[numb - 1 - g];
        str[numb - 1 - g] = temp;
    }
    for(g = 0; g < numb; g++)
        putchar(str[g]);
}
