        cout<<endl;
    }
}

//Back-end complete function Template for Java

public static void triangleWall(int s){
    for(int i = 1; i <= s; i++){
        for(int j = 1; j <= i; j++){
            System.out.print("* ");
        }
        System.out.println();
    }
}

//User function Template for C#

class Solution
{
    //Complete this function
    public void triangleWall(int s)
    {
        // Printing the triangle pattern
        for(int i=1; i<=s; i++)
        {
            for(int j=1; j<=i; j++)
            {
                Console.Write("* ");
            }
            Console.WriteLine();
        }
    }
}

