#include <iostream>
using namespace std;

int main() {
    int n;
    cin >> n;
    for (int i = 1; i <= n; i++) {
        if (i == 1) {
            cout << "* " << endl;
            continue;
        }
        if (i == n) {
            for (int j = 0; j < n; j++) {
                cout << "* ";
            }
            cout << endl;
            continue;
        }
        cout << "* ";
        for (int j = 2; j < i; j++) {
            cout << "  ";
        }
        cout << "* " << endl;
    }
}

