//Back-end complete function Template for Java
import java.util.*;

public class Main {
    public static void main(String args[]) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        for (int i = 1; i <= n; i++) {
            if (i == 1) {
                System.out.println("* ");

                continue;
            }
            if (i == n) {
                for (int j = 0; j < n; j++) {
                    System.out.print("* ");
                }
                System.out.println();
                continue;
            }
            System.out.print("* ");
            for (int j = 2; j < i; j++) {
                System.out.print("  ");
            }
            System.out.println("* ");
        }
    }
}

