//Back-end complete function Template for C

void triangle(int s) {
    for (int i = 1; i <= s; i++) {
        if (i == 1) {
            printf("* \n");
            continue;
        }
        if (i == s) {
            for (int j = 0; j < s; j++) {
                printf("* ");
            }
            printf("\n");
            continue;
        }
        printf("* ");
        for (int j = 2; j < i; j++) {
            printf("  ");
        }
        printf("* \n");
    }
}

