//Back-end complete function Template for javascript

class Solution {
    triangle(s) {
        for (let i = 1; i <= s; i++) {
            if (i === 1) {
                console.log("* ");
                continue;
            }
            if (i === s) {
                let row = "* ".repeat(s);
                console.log(row.trim());
                continue;
            }
            let line = "* ";
            line += "  ".repeat(i - 2);
            line += "* ";
            console.log(line.trim());
        }
    }
}
