{
  public:
    string gameResult (string s)
        {
            if (s[0] == s[1]) return "DRAW";


            // R,S : Winner = R
            // R,P : Winner = P
            // P,S : Winner = S
            if (s[0] == 'R')
            {
                if (s[1] == 'S')
                    return "A";
                return "B";
            }

            else if (s[0] == 'S')
            {
                if (s[1] == 'P')
                    return "A";
                return "B";
            }

            else
            {
                if (s[1] == 'R')
                    return "A";
                return "B";
            }
        }
