class Solution {
  public:
    const int M = 1000000007; // modulo

    // Function to find the first element in the Fibonacci series modulo M.
    int firstElement(int n) {
        int a = 0, b = 1, c, i;

        // if n is 0, return the first element which is 0.
        if (n == 0)
            return a;

        // iterating to find the first element in the fibonacci series.
        for (i = 2; i <= n; i++) {
            c = (a + b) % M;
            a = b % M;
            b = c % M;
        }
        return b % M;
    }
};

