//Back-end complete function template in C++

class Solution {
  public:
    //Function to encrypt a string.
    string encryptString(string S) {

        string ans = "";

        //calculating length of input string.
        int len = S.length();

        //calculating ceiling and floor of square root 
        //of length of input string.
        int c = ceil(sqrt(len));
        int f = floor(sqrt(len));

        //iterating over columns of the 2D grid.
        for(int i=0; i<c; i++){

            //iterating over rows of the 2D grid.
            for(int j=i; j<len; j+=c)

                //appending characters in the column to result string.
                ans += S[j];

            //appending space after each column.
            ans += " ";
        }

        //returning the encrypted string.
        return ans;
    }
};
