  public:

    // Function to search for a character in the string
    int searchCharacter(string &s, char ch) {

        // Iterating through the string to find the character
        for (int i = 0; i < s.length(); ++i) {
            if (s[i] == ch) {

                // Return the index of first occurrence
                return i;
            }
        }

        // Return -1 if character is not found
        return -1;
    }
};

