class Solution{
    public:
    // Function to find the position of the last person surviving in a circle of N people
    int find_pos(int N){
        // Calculating the value of m such that 2^m is the largest power of 2 less than or equal to N
        int m = floor(log(N*1.0)/ log(2.0));

        // Calculating the value of a which is equal to 2^m
        int a = pow(2, m);

        // Calculating the safe position of the last person surviving
        int safe_position = 2*(N - a) + 1;

        // Returning the safe position
        return safe_position;
    }    
};

class Solution
{
    // Function to find the position of the only set bit in the given number N.
    public int find_pos(int N)
    {
        // Calculating the value of m such that 2^m is the largest power of 2 less than or equal to N
        int m = (int)Math.Floor(Math.Log(N * 1.0) / Math.Log(2.0));

        // Calculating the value of a which is equal to 2^m
        int a = (int)Math.Pow(2, m);

        // Calculating the safe position of the last person surviving
        int safe_position = 2 * (N - a) + 1;

        // Returning the safe position
        return safe_position;
    }
}
