//Back-end complete function Template for C++

class Solution {
	public:
		//Function to find the nth term.
		int NthTerm(int n){
			long long int ans = 1;
			long long int mod = 1e9 + 7;
			//iterating from 1 to n.
			for(long long int i = 1; i <= n; i++){
				//multiplying ans with i and adding 1.
				ans = (ans*i) + 1LL;
				ans %= mod;
			}
			//returning the value of ans.
			return (int)ans;
		}

};

class Solution
{
    public int NthTerm(int n)
    {
        // initializing the modulo value
        long mod=(long)(1e9+7);

        // creating an array to store the nth terms
        long a[]=new long[n];

        // setting the first term as 2
        a[0]=2;

        // calculating the remaining terms using a loop
        for(int i=1;i<n;i++){
            // calculating the nth term based on previous term
            a[i]=((a[i-1]*(i+1))%mod+1)%mod;
        }

        // returning the nth term
        return (int)a[n-1];
    }
}

class Solution:

    def NthTerm(self, n):
        ans = 1  #basically sequence is like 5 is 2*2+1.... 16 is like 5*3+1 so it
        #like previous value*(term position )+1 so we iterative till the nth position
        mod = 10**9 + 7
        for i in range(1, n + 1):
            ans = (ans * i) + 1
            ans %= mod  #taking mod as mentioned in question
        return ans

//User function Template for C#

class Solution
{
    //Complete this function
    public int NthTerm(int n)
    {
        long ans = 1;
        long mod = 1000000007;
        //iterating from 1 to n.
        for (long i = 1; i <= n; i++)
        {
            //multiplying ans with i and adding 1.
            ans = (ans * i) + 1;
            ans %= mod;
        }
        //returning the value of ans.
        return (int)ans;
    }
}

