class Solution {
    //Function to find the nth term of the given series.
    NthTerm(n)
    {
        let ans = 1;
        const mod = 10**9 + 7;
        for (let i = 1; i <= n; i++) {
            ans = (ans * i) + 1;
            ans %= mod;
        }
        return ans;
    }
}
