class Solution {

    public static int nthTermOfAP(int a1, int a2, int n) {
        // Calculate the common difference of the arithmetic progression
        int d = a2 - a1;
        // Calculate the nth term of the arithmetic progression using the formula
        return a1 + (n - 1) * d;
    }
}

class Solution {
/**
* @param number a1
* @param number a2
* @param number n

* @returns number
*/
    nthTermOfAP(a1, a2, n) {
        let difference = a2 - a1; // Calculate the common difference between the terms.
        let nthTerm = a1 + (n - 1) * difference; // Calculate the nth term of the arithmetic progression using the formula a1 + (n - 1) * difference.
        return nthTerm; // Return the nth term of the arithmetic progression.
    }
}
