// Function to calculate the X1 series
class Solution
{
public:
    long long X1Series(int X)
    {
        // Calculating the value of ans by multiplying X with itself and then adding X to it
        long long ans = (long long)X*X*X;
        ans += X;
        // Returning the calculated answer
        return ans;
    }
};

