// Function to count the number of set bits in N
class Solution {
  public:
    int setBits(int n) {
        int count = 0; // initializing count variable to 0
        while (n) {
            n &= (n - 1); // with this operation, we remove the rightmost set bit in N
                          // and increment the count
            count++;      // incrementing the count variable by 1
        }
        return count; // returning the final count of set bits in N
    }
};

// The overall time complexity of this function is O(logN), where N is the input number.

class Solution {
    // Function to count the number of set bits in an integer
    int setBits(int n) {
        // Variable to store the count of set bits
        int count = 0;

        // Iterate till N becomes zero
        while (n > 0) {
            // Decrement N by removing the rightmost set bit and update count
            n &= (n - 1);
            count++;
        }

        // Return the count of set bits
        return count;
    }
};

class Solution:

    def setBits(self, N):
        cnt = 0

        # Loop until N is greater than 0
        while N > 0:
            # Check if N's rightmost bit is 1
            if (N & 1):
                cnt += 1

            # Right shift N by 1 bit
            N >>= 1

        # Return the count of set bits
        return cnt

