//Back-end complete function Template for C++


set<int> setInsert(int arr[],int n)
{
    set<int>s;
    for(int i=0;i<n;i++)
        s.insert(arr[i]);   //inserting array elements to set

    return s;

}

void setDisplay(set<int>s)
{
    for(auto itr=s.begin();itr!=s.end();itr++)
    {
        cout<<*itr<<" ";   //printing all set elements
    }
    cout<<endl;
}


void setErase(set<int>&s,int x)
{
    //deleting x from set if present
    if(s.count(x)!=0)
    {
        s.erase(x); 
        cout<<"erased "<<x;
    }
    else
        cout<<"not found";


    cout<<endl;
}
