// Function to check if the given string is valid or not
bool isValid(string s, int n)
{
    int i = 0, len = s.length (), j;

    // Initializing the number of bullets to the given value
    int bullets = n;

    // Iterating over the string
    for (int i = 0; i < len; ++i)
    {
        // Decrementing the number of bullets if encounter 'S'
        if (s[i] == 'S')
        {
            bullets--;

            // If the number of bullets becomes negative, return false
            if (bullets < 0) return false;
        }
        // Resetting the number of bullets to the given value if encounter 'R'
        else bullets = n;
    }
    return true;
}

