//Back-end complete function Template for C++

class Solution {
  public:
    int profit(vector<int> &arr) {
        stack<int> st;
        int ans = 0;
        for (int i = 0; i < arr.size(); i++) {
            if (st.size() == 0) {
                st.push(arr[i]);
            } else {
                while (st.size() && arr[i] >= st.top()) {
                    ans += arr[i] - st.top();
                    st.pop();
                }
                st.push(arr[i]);
            }
        }

        while (st.size()) {
            ans += st.top();
            st.pop();
        }
        return ans;
    }
};

class Solution {
    public static int profit(int v[]) {
        // Creating a Stack
        Stack<Integer> st = new Stack<>();

        // Initializing the initial profit to 0
        int ans = 0;

        // running a loop from 0 to v.size() - 1
        for (int i = 0; i < v.length; i++) {
            // if stack is empty then
            // adding the element to the stack
            if (st.size() == 0) {
                st.push(v[i]);
            } else {
                // else running a loop till we a get
                // the greatest number which is is towards
                // right of the current element and has least distance
                while (st.size() > 0 && v[i] >= st.peek()) {
                    ans += v[i] - st.peek();
                    st.pop();
                }
                st.push(v[i]);
            }
        }

        // adding all the elements which are
        // still left in the stack to the answer
        while (st.size() > 0) {
            ans += st.peek();
            st.pop();
        }

        // returing the total profit
        return ans;
    }
}

