class Solution {
    // Complete this function
    public int profit(List<int> V) {
        Stack<int> st = new Stack<int>();
        int ans = 0;
        for (int i = 0; i < V.Count; i++) {
            if (st.Count == 0) {
                st.Push(V[i]);
            } else {
                while (st.Count > 0 && V[i] >= st.Peek()) {
                    ans += V[i] - st.Peek();
                    st.Pop();
                }
                st.Push(V[i]);
            }
        }

        while (st.Count > 0) {
            ans += st.Peek();
            st.Pop();
        }
        return ans;
    }
}

