/**
 * @param {number[]} arr
 * @return {number}
 */

class Solution {
    profit(V) {
        let ans = 0;
        let stack = [];

        for (let i = 0; i < V.length; i++) {
            if (stack.length === 0) {
                stack.push(V[i]);
            } else {
                while (stack.length && V[i] >= stack[stack.length - 1]) {
                    ans += V[i] - stack[stack.length - 1];
                    stack.pop();
                }
                stack.push(V[i]);
            }
        }

        while (stack.length) {
            ans += stack[stack.length - 1];
            stack.pop();
        }
        return ans;
    }
}
