solutions.full_func:
class Solution{
    public:
    string shortestPath (string s)
    {
        int ver = 0, hor = 0;


        // calculating the horizontal and vertical displacement
        for (int i = 0; i < s.length (); ++i)
        {
            if (s[i] == 'N') ver++;
            else if (s[i] == 'S') ver--;

            else if (s[i] == 'W') hor++;
            else hor--;
        }

        string res = "";

        // if hor < 0, we move east
        while (hor < 0)
        {
            res += "E";
            hor++;
        }

        // if ver > 0, we move north
        while (ver > 0)
        {
            res += "N";
            ver--;
        }

        // if ver < 0, we move south
        while (ver < 0)
        {
            res += "S";
            ver++;
        }

        // if hor > 0, we move west
        while (hor > 0)
        {
            res += "W";
            hor--;
        }

        // note that we do not need to sort the string as
        // we already appended characters in the sorted order
        // ENSW
        return res;
    }
};
