/**
 * @param {number} P
 * @param {number} R
 * @param {number} T
 * @return {number}
 */

class Solution {
    simpleInterest(P, R, T) {
        let prt = P * R * T * 0.01;
        let prtString = prt.toString();
        return eval(prtString).toFixed(2);
    }
}
