// Back-end complete function template in C++
class Solution
{
    public:
    // Function to find factorial of given number
    long long int find_fact(int n)
    {
        long long int ans = 1;

        // Loop to calculate factorial
        for(int i = 1; i <= n; i++)
            ans *= i;

        return ans; // Returning the factorial
    }
};

class Solution {
