//Back-end complete function template in C++

class Solution{
    public:
    //Function to find nearest power of 2 of a given number N.
    long long nearestPowerOf2(long long N){
        //initialize counter variable to count the number of shifts needed.
        long long c=0;

        //decrementing N by 1 to get the nearest power of 2 lower than N.
        N--;

        //looping until N is non-zero, and left shifting N by 1 and incrementing counter c.
        while(N)
        {
            N/=2;
            c++;
        }

        //returning the nearest power of 2 using left shift operator to shift 1 to the left by c positions.
        return (1LL<<c);
    }
};

