//Back-end complete function Template for Java

class Solution{

    // function to find the nearest power of 2 less than or equal to N
    static long nearestPowerOf2(long N){
        long c=0;
        N--;
        while(N>0){
            N/=2;
            c++;
        }
        return ((long)1<<c);
    }
}

