class Solution {
  public:
    //Function to convert the given string to snake_case.
    string snakeCase(string S , int n) {
        string ans="";

        // We simply traverse through the Charaters of the String and change
        // Uppercase characters to lowercase and change whitespaces to '_'

        for(int i=0 ; i<n ; i++)
        {
            // if character is uppercase, add 32 to its ASCII value 
            // to convert it to lowercase.
            if(isupper(S[i]))
                ans+=(S[i]+32);
            // if character is whitespace, replace it with '_'
            else if(S[i]==' ')
                ans+='_';
            // otherwise, add the character as it is to the string.
            else
                ans+=S[i];
        }

        return ans;
    }
};

class Solution {
    static String snakeCase(String S , int n) {
        String ans="";

        // We simply traverse through the Charaters of the String and change
        // Uppercase characters to lowercase and change whitespaces to '_'
        for(int i=0 ; i<n ; i++)
        {
            if(Character.isUpperCase(S.charAt(i))) // check if character is uppercase
                ans+=Character.toLowerCase(S.charAt(i)); // convert uppercase to lowercase
            else if(S.charAt(i)==' ') // check if character is whitespace
                ans+='_'; // replace whitespace with '_'
            else
                ans+=S.charAt(i); // keep the character as it is
        }

        return ans;
    }
};

class Solution:
    def snakeCase(self, S , n):

        ans=""

        # We simply traverse through the Charaters of the String and change
        # Uppercase characters to lowercase and change whitespaces to '_'

        for i in range(n):
            # if character is uppercase, convert it to lowercase
            if S[i].isupper():
                ans+=S[i].lower()
            # if character is whitespace, replace it with '_'
            elif S[i]==" ":
                ans+='_'
            else:
                ans+=S[i]

        return ans
