class Solution{
	public:
   	// Function to find Sophie primes up to n
   	vector<int> sophie_Primes(int n){
   		// Create a boolean array to mark the numbers as prime or not
   		vector<bool>vis(2*n + 2, true);

   		// Sieve of Eratosthenes algorithm to mark non-prime numbers
   		for(int i = 2; i * i <= (2 * n + 1); i++){
   			if(vis[i]){
   				for(int j = i*i; j <= (2 * n + 1); j+=i){
   					vis[j] = false;
   				}
   			}
   		}

   		// Storing Sophie primes in a vector
   		vector<int>ans;
   		for(int i = 2; i < n; i++)
   			// Check if both i and 2*i+1 are prime
   			if(vis[i] and vis[2*i+1])
   				// Add i to the answer vector
   				ans.push_back(i);

   		// Return the vector containing Sophie primes
   		return ans;
   	}    
};

class Solution
{
    public List<int> sophie_Primes(int n)
    {
        // Create a boolean array to mark the numbers as prime or not
        List<bool> vis = new List<bool>(2 * n + 2);

        // Sieve of Eratosthenes algorithm to mark non-prime numbers
        for (int i = 0; i <= 2 * n + 1; i++)
        {
            vis.Add(true);
        }

        for (int i = 2; i * i <= (2 * n + 1); i++)
        {
            if (vis[i])
            {
                for (int j = i * i; j <= (2 * n + 1); j += i)
                {
                    vis[j] = false;
                }
            }
        }

        // Storing Sophie primes in a list
        List<int> ans = new List<int>();
        for (int i = 2; i < n; i++)
        {
            // Check if both i and 2*i+1 are prime
            if (vis[i] && vis[2 * i + 1])
            {
                // Add i to the answer list
                ans.Add(i);
            }
        }

        // Return the list containing Sophie primes
        return ans;
    }
}
