//Back-end complete function template for C++

//Function to sort the given vector in ascending order.
vector<int> sortVector(vector<int>v)
{
    sort(v.begin(),v.end());
    return v;
}

//Function to reverse the order of elements in the given vector.
vector<int> reverseVector(vector<int>v)
{
    reverse(v.begin(),v.end());
    return v;
}
