int diff = 0;

// Function to compare values while sorting.
bool mycomparator(int a, int b) {

    // storing the difference of first number with diff.
    int a_diff = abs(a - diff);

    // storing the difference of second number with diff.
    int b_diff = abs(b - diff);

    // if a_diff is smaller than b_diff, it means we need to put a_diff
    // so we return true.
    if (a_diff < b_diff)
        return 1;

    // else we return false.
    return 0;
}

class Solution {
  public:
    // Function to sort the array according to difference with given number.
    void sortABS(int k, vector<int> &arr) {
        diff = k;
        // using stable sort function over the array.
        stable_sort(arr.begin(), arr.end(), mycomparator);
    }
};

class Solution {
    // Function to sort the list according to difference with the given number.
    public static void sortABS(int k, List<Integer> list) {
        // Sorting the list using a custom comparator.
        Collections.sort(list, new Comparator<Integer>() {
            @Override
            public int compare(Integer a, Integer b) {
                return Integer.compare(Math.abs(a - k), Math.abs(b - k));
            }
        });
    }
}

class Solution:

    def sortABS(self, k, arr):
        arr.sort(key=lambda x: abs(x - k))

class Solution {
    sortABS(k, arr) { arr.sort((a, b) => Math.abs(a - k) - Math.abs(b - k)); }
}
