class Solution {
  public:
    void freqSorted(vector<int>& arr) {
        // Declaring and initializing a new map
        map<int, int> mp;

        // Incrementing the previous value if key is already present
        // else initializing the key to value 1
        for (int num : arr) {
            mp[num]++;
        }

        // Traversing the map and printing the key-value pairs
        for (const auto& entry : mp) {
            int key = entry.first;
            int value = entry.second;
            std::cout << key << " " << value << std::endl;
        }
    }
};

