class Solution
{
    
public:
    // Function to convert a number from base 10 to base 6
    int getSpecialNumber(int N)
    {
        N--; // Decrement N by 1 to get the base 6 representation
        
        long long int p10=1LL; // Variable to store powers of 10
        long long int N_b6 = 0LL; // Variable to store the converted number in base 6
        
        // Converting from base 10 to base 6
        while (N) 
        {
            N_b6 += p10 * (N % 6); // Adding the digit in base 6 to N_b6
            N /= 6; // Updating N by dividing it by 6
            p10 *= 10; // Updating p10 by multiplying it by 10 to consider the next digit
        }
    
        return N_b6; // Returning the number in base 6    
    }

};

