class Solution
{
    //Method to calculate and return the special number based on input N.
    public long getSpecialNumber(int N)
    {
        //Since the numbering starts from 0, decrement N by 1.
        N--;

        //Initialize the variable to store base 6 number as 0.
        long N_b6 = 0;

        //Initialize the base 10 value to calculate the base 6 number.
        long p10 = 1;

        //Loop to change the base from 10 to 6.
        while (N != 0) 
        {
            //Calculate the base 6 digit and add it to the base 6 number.
            N_b6 += p10 * (N % 6);

            //Reduce N to next digit by dividing it by 6.
            N /= 6;

            //Increase the power of 10 to calculate the next digit position.
            p10 *= 10;
        }

        //Return the calculated special number in base 6.
        return N_b6;    
    }
}
