//Back-end complete function Template for C++
class Solution {
  public:
    //Function to calculate the sum of the series.
    long long int sumOfTheSeries(long long int n){
    // required sum
    return (n * (n + 1) / 2) *
            (2 * n + 1) / 3;
}
};

