class Solution {
  public:
    bool a[100000];
    int primes[100000], in = 0;

    void Sieve(int n) {
        memset(a, true, sizeof(a));
        a[1] = false;
        for (int i = 2; i * i <= n; i++) {
            if (a[i]) {
                for (int j = 2 * i; j <= n; j += i)
                    a[j] = false;
            }
        }
        for (int i = 2; i <= n; i++) {
            if (a[i])
                primes[in++] = i;
        }
    }

    int calc(int in, int cur, int k) {
        int newCur = primes[in] * primes[in] * cur;
        int res = 0;
        if (newCur > k) // case when [k/newCur]=0, so we dont need to recurse any
                        // further
            return 0;
        // include this to answer
        res += k / (newCur);
        res += calc(in + 1, cur, k);

        // exclude from answer the repeated ones
        res -= calc(in + 1, newCur, k);
        return res;
    }

    int sqNum(int n) {

        Sieve(100000);

        int ans = calc(0, 1, n);

        return ans;
    }
};
