//Back-end complete function Template for C++
class Solution {
  public:

    // Function to calculate the difference between
    // the sum of squares and square of sum
    long long int squaresDiff(int N)
    {
        // Calculate the sum of squares using formula
        long long int l = (N * (N + 1) * (2 * N + 1)) / 6;

        // Calculate the square of sum using formula
        long long int k = (N * (N + 1)) / 2;
        k = k *k;

        // Return the absolute difference between the
        // sum of squares and square of sum
        return abs(l - k);
    }
};
