//Back-end complete function Template for C++

void isDivisibleByPrime(int n){

    //checking if n is divisible by 11
    if(n%11 == 0){
        cout << "Eleven" << endl;
    }

    //checking if n is divisible by 2 or 3
    else if(n%2 == 0 || n%3 == 0){

        //checking if n is divisible by 3
        if(n%3 == 0){
            cout << "Three" << endl;
        }
        //checking if n is divisible by 2
        else if(n%2 == 0){
            cout << "Two" << endl;
        }
    }
    //if none of the conditions are satisfied, print -1
    else{
        cout << "-1" << endl;
    }
}

// Back-end complete function Template for javascript

class Solution {
    stepByStep(n) {
        if(n%11 === 0){ // Check if n is divisible by 11.
            console.log("Eleven"); // Print "Eleven" to the console if n is divisible by 11.
        }
        else if(n%2 === 0 || n%3 === 0){ // Check if n is divisible by either 2 or 3.
            if(n%3 === 0){ // Check if n is divisible by 3.
                console.log("Three"); // Print "Three" to the console if n is divisible by 3.
            }
            else if(n%2 === 0){ // Check if n is divisible by 2.
                console.log("Two"); // Print "Two" to the console if n is divisible by 2.
            }
        }
        else{
            console.log("-1"); // Print -1 to the console if none of the above conditions are met.
        }
    }
}

