//Back-end complete function template for C++

class Solution{
public:


	//Function to check if the given string is equal to a number within the range of its length.
	int isSame(string str)
	{
	    int n = str.size();


	    int num = 0, x = 1, i = n-1;

	    //iterating over the string from the end.
	    for (i = n-1; i >= 0; i--)
	    {
	        //checking if the character is a digit.
	        if ('0' <= str[i] &&  str[i]  <= '9')
	        {
	            //calculating the number by converting the digit characters to integers.
	            num = (str[i] - '0')*x + num;

	            //incrementing x for each additional digit.
	            x = x * 10;
	        }
	        //if the character is not a digit, break the loop.
	        else 
	        	break;

	        //if num becomes greater than the length of the string, break the loop.
	        if(num > n)
	        	break;
	    }

	    //returning whether the calculated number is equal to i+1.
	    return (num == i+1);
	}
};

