// Function to compare two pairs based on marks
bool compare(pair<string, int> p1, pair<string, int> p2){
    if(p1.second == p2.second){
        return p1.first < p2.first;  // If marks are same, sort by name
    }
    else
    return p1.second > p2.second;  // Sort in descending order based on marks
}

/* Function to sort students with respect to their marks
* v : vector input with student name and their marks
* N : size of vector
*/
vector<pair<string, int>> sortMarks(vector<pair<string, int>> v, int N){

    sort(v.begin(), v.end(), compare);   // Sort the vector based on the compare function

    return v;   // Return the sorted vector

}
