class Solution {
  public:
    // Function to count the number of subarrays with at most K distinct elements
    int atMostK(vector<int> &arr, int k) {
        int i = 0, res = 0;
        unordered_map<int, int> count;
        for (int j = 0; j < arr.size(); ++j) {
            // If the element at index j is encountered for the first time, decrement K
            if (!count[arr[j]]++)
                k--;

            // If the number of distinct elements exceeds K, move the left pointer i and
            // increment K
            while (k < 0) {
                if (!--count[arr[i]])
                    k++;
                i++;
            }
            // Add the number of subarrays that can be formed with the current window
            res += j - i + 1;
        }
        return res;
    }
};

class Solution {
    public int atMostK(int[] arr, int k) {
        int i = 0, res = 0;
        Map<Integer, Integer> count = new HashMap<>();
        for (int j = 0; j < arr.length; ++j) {
            // If the element at index j is encountered for the first time, decrement K
            count.put(arr[j], count.getOrDefault(arr[j], 0) + 1);
            if (count.get(arr[j]) == 1) k--;

            // If the number of distinct elements exceeds K, move the left pointer i and
            // increment K
            while (k < 0) {
                count.put(arr[i], count.get(arr[i]) - 1);
                if (count.get(arr[i]) == 0) k++;
                i++;
            }
            // Add the number of subarrays that can be formed with the current window
            res += j - i + 1;
        }
        return res;
    }
}

