class Solution {
    public:
    // Function to find the modified GCD using repeated subtraction.
    int modifiedGcd(int A,int B){
        if(A%B==0)
         return A/B;
        return A/B+modifiedGcd(B,A%B); 
    } 

    // Function to find the result using the modified GCD function.
    int repeatedSubtraction(int A,int B){
        return modifiedGcd(A,B);
    }
};

